import React, { useEffect, useState } from 'react';

function SistemaCP() {
  return (
    <main>
      <FormProduto />
    </main>
  );
}

function FormProduto() {
  const [produtos, setProdutos] = useState([]);
  const [nome, setNome] = useState("");
  const [descricao, setDescricao] = useState("");
  const [marca, setMarca] = useState("");
  const [fabricante, setFabricante] = useState("");
  const [validadeProduto, setValidadeProduto] = useState("");
  const [peso, setPeso] = useState("");
  const [preco, setPreco] = useState("");
  const [categoriaID, setCategoriaID] = useState("");
  const [fornecedorID, setFornecedorID] = useState("");
  const [categorias, setCategorias] = useState([]);
  const [fornecedores, setFornecedores] = useState([]);
  const [filterFornecedores, setFilterFornecedores] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [mensagem, setMensagem] = useState("");

  useEffect(() => {
    fetchProdutos();
    fetchCategorias();
    fetchFornecedores();
  }, []);

  const fetchProdutos = async () => {
    try {
      const response = await fetch('https://localhost:59226/api/Produtos');
      if (!response.ok) {
        throw new Error('Erro ao buscar produtos');
      }
      const data = await response.json();
      setProdutos(data);
    } catch (error) {
      console.error('Erro ao buscar produtos:', error);
    }
  };

  const fetchCategorias = async () => {
    try {
      const response = await fetch('https://localhost:59226/api/Produtos');
      if (!response.ok) {
        throw new Error('Erro ao buscar categorias');
      }
      const data = await response.json();
      setCategorias(data);
    } catch (error) {
      console.error('Erro ao buscar categorias:', error);
    }
  };

  const fetchFornecedores = async () => {
    try {
      const response = await fetch('https://localhost:59226/api/Produtos');
      if (!response.ok) {
        throw new Error('Erro ao buscar fornecedores');
      }
      const data = await response.json();
      setFornecedores(data);
    } catch (error) {
      console.error('Erro ao buscar fornecedores:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newProduto = {
      nome,
      descricao,
      marca,
      fabricante,
      validadeProduto,
      peso: Number(peso),
      preco: Number(preco),
      categoriaID: Number(categoriaID),
      fornecedorID: Number(fornecedorID),
    };

    try {
      const response = await fetch('https://localhost:59226/api/Produtos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newProduto),
      });

      if (!response.ok) {
        throw new Error('Erro ao cadastrar produto');
      }

      setMensagem("Produto cadastrado com sucesso!");
      clearForm();
      fetchProdutos();
    } catch (error) {
      console.error('Erro ao cadastrar produto:', error);
    }
  };

  const handleDelete = async (index) => {
    const produtoToDelete = produtos[index];
    try {
      const response = await fetch(`https://localhost:59226/api/Produtos/${produtoToDelete.id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Erro ao excluir produto');
      }

      fetchProdutos();
    } catch (error) {
      console.error('Erro ao excluir produto:', error);
    }
  };

  const handleEdit = (index) => {
    const produtoToEdit = produtos[index];
    setNome(produtoToEdit.nome);
    setDescricao(produtoToEdit.descricao);
    setMarca(produtoToEdit.marca);
    setFabricante(produtoToEdit.fabricante);
    setValidadeProduto(produtoToEdit.validadeProduto || "");
    setPeso(produtoToEdit.peso.toString());
    setPreco(produtoToEdit.preco.toString());
    setCategoriaID(produtoToEdit.categoriaID.toString());
    setFornecedorID(produtoToEdit.fornecedorID.toString());
    setIsEditing(true);
  };

  const handleSave = async (e) => {
    e.preventDefault();
    const updatedProduto = {
      nome,
      descricao,
      marca,
      fabricante,
      validadeProduto,
      peso: Number(peso),
      preco: Number(preco),
      categoriaID: Number(categoriaID),
      fornecedorID: Number(fornecedorID),
    };

    try {
      const response = await fetch(`https://localhost:59226/api/Produtos/${produtos[index].id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedProduto),
      });

      if (!response.ok) {
        throw new Error('Erro ao atualizar produto');
      }

      setMensagem("Produto atualizado com sucesso!");
      clearForm();
      fetchProdutos();
    } catch (error) {
      console.error('Erro ao atualizar produto:', error);
    }
  };

  const clearForm = () => {
    setNome("");
    setDescricao("");
    setMarca("");
    setFabricante("");
    setValidadeProduto("");
    setPeso("");
    setPreco("");
    setCategoriaID("");
    setFornecedorID("");
    setIsEditing(false);
  };

  return (
    <div className="form-produtos">
      <form onSubmit={isEditing ? handleSave : handleSubmit}>
        <h1>Sistema de Cadastro de Produtos</h1>
        <div className="field">
          <strong>Nome do Produto:</strong>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            placeholder="Ex: Adidas Aires"
            required
          />
        </div>
        <div className="field">
          <strong>Descrição do Produto</strong>
          <input
            type="text"
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
            placeholder="Ex: Tênis Tamanho: 41-42"
            required
          />
        </div>
        <div className="field">
          <strong>Marca do Produto</strong>
          <input
            type="text"
            value={marca}
            onChange={(e) => setMarca(e.target.value)}
            placeholder="Ex: Adidas, Nike"
            required
          />
        </div>
        <div className="field">
          <strong>Fabricante do Produto</strong>
          <input
            type="text"
            value={fabricante}
            onChange={(e) => setFabricante(e.target.value)}
            placeholder="Ex: Nike - Industrias Brasileiras"
            required
          />
        </div>
        <div className="field">
          <strong>Validade do Produto</strong>
          <input
            type="text"
            value={validadeProduto}
            onChange={(e) => setValidadeProduto(e.target.value)}
            placeholder="Ex: 20 de maio 2025 - (Opcional)"
          />
        </div>
        <div className="field">
          <strong>Peso do Produto</strong>
          <input
            type="number"
            value={peso}
            onChange={(e) => setPeso(e.target.value)}
            placeholder="Ex: 125"
            required
          />
        </div>
        <div className="field">
          <strong>Preço do Produto</strong>
          <input
            type="number"
            value={preco}
            onChange={(e) => setPreco(e.target.value)}
            placeholder="Ex: 123"
            required
          />
        </div>
        <div className="field">
          <strong>Categoria do Produto</strong>
          <select
            value={categoriaID}
            onChange={(e) => setCategoriaID(e.target.value)}
            required
          >
            <option value="">Selecione a Categoria</option>
            {categorias.map((categoria) => (
              <option key={categoria.id} value={categoria.id}>
                {categoria.nome}
              </option>
            ))}
          </select>
        </div>
        <div className="field">
          <strong>Fornecedor</strong>
          <select
            value={fornecedorID}
            onChange={(e) => setFornecedorID(e.target.value)}
            required
          >
            <option value="">Selecione o Fornecedor</option>
            {filterFornecedores.map((fornecedor) => (
              <option key={fornecedor.id} value={fornecedor.id}>
                {fornecedor.nome}
              </option>
            ))}
          </select>
        </div>
        {mensagem && <div className="mensagem-sucesso">{mensagem}</div>}
        <div className="btn-actions">
          <button type="submit">{isEditing ? "Salvar Produto" : "Adicionar Produto"}</button>
        </div>
      </form>
      <ProdutoLista
        produtos={produtos}
        categorias={categorias}
        fornecedores={fornecedores}
        handleDelete={handleDelete}
        handleEdit={handleEdit}
      />
    </div>
  );
}

function ProdutoLista({ produtos, categorias, fornecedores, handleDelete, handleEdit }) {
  return (
    <div className="lista-produto">
      <h2>Produtos</h2>
      {produtos.map((produto, index) => (
        <div key={index} className="product-item">
          <p><strong>Nome do Produto:</strong> {produto.nome}</p>
          <p><strong>Descrição do Produto:</strong> {produto.descricao}</p>
          <p><strong>Marca do Produto:</strong> {produto.marca}</p>
          <p><strong>Fabricante do Produto:</strong> {produto.fabricante}</p>
          <p><strong>Validade do Produto:</strong> {produto.validadeProduto}</p>
          <p><strong>Peso do Produto:</strong> {produto.peso} kg</p>
          <p><strong>Preço do Produto:</strong> R$ {produto.preco}</p>
          <p><strong>Categoria do Produto:</strong> {getCategoriaNome(categorias, produto.categoriaID)}</p>
          <p><strong>Fornecedor:</strong> {getFornecedorNome(fornecedores, produto.fornecedorID)}</p>
          <button className="delete-btn" onClick={() => handleDelete(index)}>Excluir</button>
          <button className="edit-btn" onClick={() => handleEdit(index)}>Editar</button>
        </div>
      ))}
    </div>
  );
}

function getCategoriaNome(categorias, categoriaID) {
  const categoria = categorias.find(cat => cat.id === parseInt(categoriaID));
  return categoria ? categoria.nome : "Categoria não encontrada";
}

function getFornecedorNome(fornecedores, fornecedorID) {
  const fornecedor = fornecedores.find(fornecedor => fornecedor.id === parseInt(fornecedorID));
  return fornecedor ? fornecedor.nome : "Fornecedor não encontrado";
}

export default SistemaCP;
