import { createStore, combineReducers } from 'redux';

const initialProductState = {
    produtos: []
};

const produtoReducer = (state = initialProductState, action) => {
    switch (action.type) {
        case 'SET_PRODUTOS':
            return {
                ...state,
                produtos:  action.payload
            };
        case 'DELETE':
            return {
                ...state,
                produtos: state.produtos.filter((_, index) => index !== action.payload)
            };
        case 'PUT':
            return {
                ...state,
                produtos: state.produtos.map((produto, id) => 
                id === action.payload.id ? action.payload.updatedProduto : produto)
            };
        default:
            return state;
    }
};

const rootReducer = combineReducers({
    produto: produtoReducer
});

const store = createStore(rootReducer);

export default store;
