import React, { useEffect, useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';

const API_URL = "https://localhost:54438/api/Produtos"; // Certifique-se de que a porta estÃ¡ correta

function SistemaCP() {
  return (
    <main>
      <FormProduto />
    </main>
  );
}

function FormProduto() {
  const dispatch = useDispatch();
  const produtos = useSelector(state => state.produto.produtos);

  const [nome, setNome] = useState("");
  const [descricao, setDescricao] = useState("");
  const [marca, setMarca] = useState("");
  const [fabricante, setFabricante] = useState("");
  const [peso, setPeso] = useState("");
  const [preco, setPreco] = useState("");
  const [categoriaID, setCategoriaID] = useState("");
  const [fornecedorID, setFornecedorID] = useState("");
  const [filterFornecedores, setFilterFornecedores] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [mensagem, setMensagem] = useState("");
  const [editIndex, setEditIndex] = useState(null);

  useEffect(() => {
    if (mensagem) {
      const timer = setTimeout(() => {
        setMensagem("");
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [mensagem]);

  useEffect(() => {
    if (categoriaID) {
      const fornecedoresFiltrados = fornecedores.filter(fornecedor =>
        fornecedor.categorias.includes(parseInt(categoriaID))
      );
      setFilterFornecedores(fornecedoresFiltrados);
      setFornecedorID("");
    } else {
      setFilterFornecedores([]);
    }
  }, [categoriaID]);

  const fetchProdutos = useCallback(async () => {
    try {
      const response = await fetch(API_URL);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log("Dados Recebidos:", data);
      dispatch({ type: 'SET_PRODUTOS', payload: data });
    } catch (error) {
      console.error("Failed to fetch produtos:", error);
    }
  }, [dispatch]);

  useEffect(() => {
    fetchProdutos();
  }, [fetchProdutos]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newProduto = {
      nome,
      descricao,
      marca,
      fabricante,
      peso,
      preco,
      categoriaID,
      fornecedorID
    };

    try {
      if (isEditing) {
        await fetch(`${API_URL}/${editIndex}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newProduto)
        });
        dispatch({ type: 'PUT', payload: { id: editIndex, updatedProduto: newProduto } });
        setMensagem("Produto atualizado com sucesso!");
      } else {
        const response = await fetch(API_URL, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newProduto)
        });
        const addedProduto = await response.json();
        dispatch({ type: 'ADD', payload: addedProduto });
        setMensagem("Produto cadastrado com sucesso!");
      }
      fetchProdutos();
      clearForm();
    } catch (error) {
      console.error("Failed to save produto:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`${API_URL}/${id}`, {
        method: 'DELETE'
      });
      dispatch({ type: 'DELETE', payload: id });
      fetchProdutos();
    } catch (error) {
