import React, { useEffect, useState, useCallback } from 'react';
import { useDispatch, useSelector } from 'react-redux';

const API_URL = "https://localhost:52153/api/Produtos"; // Certifique-se de que a porta está correta

function SistemaCP() {
  return (
    <main>
      <FormProduto />
    </main>
  );
}

function FormProduto() {
  const dispatch = useDispatch();
  const produtos = useSelector(state => state.produto.produtos);

  const [nome, setNome] = useState("");
  const [descricao, setDescricao] = useState("");
  const [marca, setMarca] = useState("");
  const [fabricante, setFabricante] = useState("");
  const [peso, setPeso] = useState("");
  const [preco, setPreco] = useState("");
  const [categoriaID, setCategoriaID] = useState("");
  const [fornecedorID, setFornecedorID] = useState("");
  const [filterFornecedores, setFilterFornecedores] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [mensagem, setMensagem] = useState("");
  const [editIndex, setEditIndex] = useState(null);

  useEffect(() => {
    if (mensagem) {
      const timer = setTimeout(() => {
        setMensagem("");
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [mensagem]);

  useEffect(() => {
    if (categoriaID) {
      const fornecedoresFiltrados = fornecedores.filter(fornecedor =>
        fornecedor.categorias.includes(parseInt(categoriaID))
      );
      setFilterFornecedores(fornecedoresFiltrados);
      setFornecedorID("");
    } else {
      setFilterFornecedores([]);
    }
  }, [categoriaID]);

  const fetchProdutos = useCallback(async () => {
    try {
      const response = await fetch(API_URL);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log("Dados Recebidos:", data);
      dispatch({ type: 'SET_PRODUTOS', payload: data });
    } catch (error) {
      console.error("Failed to fetch produtos:", error);
    }
  }, [dispatch]);

  useEffect(() => {
    fetchProdutos();
  }, [fetchProdutos]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newProduto = {
      nome,
      descricao,
      marca,
      fabricante,
      peso,
      preco,
      categoriaID,
      fornecedorID
    };

    try {
      if (isEditing) {
        console.log("Dados enviados para atualização:", newProduto);
        const response = await fetch(`${API_URL}/${editIndex}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newProduto)
        });
        if (response.ok) {
          setMensagem("Produto atualizado com sucesso!");
          fetchProdutos();
          clearForm();
        } else {
          const errorMessage = await response.text();
          throw new Error(`Erro ao atualizar produto: ${response.status} - ${errorMessage}`);
        }
      } else {
        console.log("Dados enviados para criação:", newProduto);
        const response = await fetch(API_URL, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(newProduto)
        });
        if (response.ok) {
          setMensagem("Produto cadastrado com sucesso!");
          fetchProdutos();
          clearForm();
        } else {
          const errorMessage = await response.text();
          throw new Error(`Erro ao cadastrar produto: ${response.status} - ${errorMessage}`);
        }
      }
    } catch (error) {
      console.error("Failed to save produto:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      const response = await fetch(`${API_URL}/${id}`, {
        method: 'DELETE'
      });
      if (response.ok) {
        fetchProdutos();
      } else {
        throw new Error(`Erro ao excluir produto: ${response.status}`);
      }
    } catch (error) {
      console.error("Failed to delete produto:", error);
    }
  };

  const handleEdit = (id) => {
    const produtoToEdit = produtos.find(produto => produto.id === id);
    setNome(produtoToEdit.nome);
    setDescricao(produtoToEdit.descricao);
    setMarca(produtoToEdit.marca);
    setFabricante(produtoToEdit.fabricante);
    setPeso(produtoToEdit.peso);
    setPreco(produtoToEdit.preco);
    setCategoriaID(produtoToEdit.categoriaID);
    setFornecedorID(produtoToEdit.fornecedorID);
    setEditIndex(produtoToEdit.id);
    setIsEditing(true);
  };

  const clearForm = () => {
    setNome("");
    setDescricao("");
    setMarca("");
    setFabricante("");
    setPeso("");
    setPreco("");
    setCategoriaID("");
    setFornecedorID("");
    setIsEditing(false);
    setEditIndex(null);
  };

  return (
    <div className="form-produtos">
      <form onSubmit={handleSubmit}>
        <h1>Sistema de Cadastro de Produtos</h1>
        <div className="field">
          <strong>Nome do Produto:</strong>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            placeholder="Ex: Adidas Aires"
            required />
        </div>
        <div className="field">
          <strong>Descrição do Produto</strong>
          <input
            type="text"
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
            placeholder="Ex: Tênis Tamanho: 41-42"
            required />
        </div>
        <div className="field">
          <strong>Marca do Produto</strong>
          <input
            type="text"
            value={marca}
            onChange={(e) => setMarca(e.target.value)}
            placeholder="Ex: Adidas, Nike"
            required />
        </div>
        <div className="field">
          <strong>Fabricante do Produto</strong>
          <input
            type="text"
            value={fabricante}
            onChange={(e) => setFabricante(e.target.value)}
            placeholder="Ex: Nike - Industrias Brasileiras"
            required />
        </div>
        <div className="field">
          <strong>Peso do Produto</strong>
          <input
            type="number"
            value={peso}
            onChange={(e) => setPeso(e.target.value)}
            placeholder="Ex: 125"
            required />
        </div>
        <div className="field">
          <strong>Preço do Produto</strong>
          <input
            type="number"
            value={preco}
            onChange={(e) => setPreco(e.target.value)}
            placeholder="Ex: 123"
            required />
        </div>
        <div className="field">
          <strong>Categoria do Produto</strong>
          <select
            value={categoriaID}
            onChange={(e) => setCategoriaID(e.target.value)}
            required>
            <option value="">Selecione a Categoria</option>
            {categorias.map((categoria) => (
              <option key={categoria.id} value={categoria.id}>
                {categoria.nome}
              </option>
            ))}
          </select>
        </div>
        <div className="field">
          <strong>Fornecedor</strong>
          <select
            value={fornecedorID}
            onChange={(e) => setFornecedorID(e.target.value)}
            required>
            <option value="">Selecione o Fornecedor</option>
            {filterFornecedores.map((fornecedor) => (
              <option key={fornecedor.id} value={fornecedor.id}>{fornecedor.nome}</option>
            ))}
          </select>
        </div>
        {mensagem && <div className="mensagem-sucesso">{mensagem}</div>}
        <button type="submit">{isEditing ? "Atualizar Produto" : "Cadastrar Produto"}</button>
      </form>
      {produtos.length === 0 ? (
        <div>Carregando produtos...</div>
      ) : (
        <ProductList produtos={produtos} handleDelete={handleDelete} handleEdit={handleEdit} />
      )}
    </div>
  );
}

function ProductList({ produtos, handleDelete, handleEdit }) {
  return (
    <div className="product-list">
      {produtos.map((produto) => (
        <div key={produto.id} className="product-item">
          <p><strong>Nome do Produto:</strong> {produto.nome}</p>
          <p><strong>Descrição do Produto:</strong> {produto.descricao}</p>
          <p><strong>Marca do Produto:</strong> {produto.marca}</p>
          <p><strong>Fabricante do Produto:</strong> {produto.fabricante}</p>
          <p><strong>Peso do Produto:</strong> {produto.peso} kg</p>
          <p><strong>Preço do Produto:</strong> R$ {produto.preco}</p>
          <p><strong>Categoria do Produto:</strong> {getCategoriaNome(categorias, produto.categoriaID)}</p>
          <p><strong>Fornecedor:</strong> {getFornecedorNome(fornecedores, produto.fornecedorID)}</p>
          <button className="delete-btn" onClick={() => handleDelete(produto.id)}>Excluir</button>
          <button className="edit-btn" onClick={() => handleEdit(produto.id)}>Editar</button>
        </div>
      ))}
    </div>
  );
}

function getCategoriaNome(categorias, categoriaID) {
  const categoria = categorias.find(cat => cat.id === parseInt(categoriaID));
  return categoria ? categoria.nome : "Categoria não encontrada";
}

function getFornecedorNome(fornecedores, fornecedorID) {
  const fornecedor = fornecedores.find(fornecedor => fornecedor.id === parseInt(fornecedorID));
  return fornecedor ? fornecedor.nome : "Fornecedor não encontrado";
}

const categorias = [
  { id: 1, nome: "Eletrônicos" },
  { id: 2, nome: "Roupas" },
  { id: 3, nome: "Alimentos" },
  { id: 4, nome: "Acessórios Esportivos" }
];

const fornecedores = [
  { id: 1, nome: "Fornecedor de Eletrônicos", categorias: [1] },
  { id: 2, nome: "Fornecedor de Roupas", categorias: [2] },
  { id: 3, nome: "Fornecedor de Alimentos", categorias: [3] },
  { id: 4, nome: "Fornecedor de Equipamentos Esportivos", categorias: [4] }
];

export default SistemaCP;
