import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

const apiUrl = 'https://localhost:59226/api/Produtos'; // Substitua pelo URL da sua API

function SistemaCP(){
  return(
    <main>
      <FormProduto />
    </main>
  );
}

function FormProduto(){
  const dispatch = useDispatch();
  const produtos = useSelector(state => state.produto.produtos);
  const [categorias, setCategorias] = useState([]);
  const [fornecedores, setFornecedores] = useState([]);

  useEffect(() => {
    fetch(apiUrl)
      .then(response => response.json())
      .then(data => {
        dispatch({ type: 'SET_PRODUTOS', payload: data });
      })
      .catch(error => console.error('Erro ao carregar produtos:', error));

    fetch('hhttps://localhost:59226/api/Produtos')
      .then(response => response.json())
      .then(data => {
        setCategorias(data);
      })
      .catch(error => console.error('Erro ao carregar categorias:', error));

    fetch('https://localhost:59226/api/Produtos')
      .then(response => response.json())
      .then(data => {
        setFornecedores(data);
      })
      .catch(error => console.error('Erro ao carregar fornecedores:', error));
  }, [dispatch]);

  const [nome, setNome] = useState("");
  const [descricao, setDescricao] = useState("");
  const [marca, setMarca] = useState("");
  const [fabricante, setFabricante] = useState("");
  const [validadeProduto, setValidadeProduto] = useState("");
  const [peso, setPeso] = useState("");
  const [preco, setPreco] = useState("");
  const [categoriaID, setCategoriaID] = useState("");
  const [fornecedorID, setFornecedorID] = useState("");
  const [filterFornecedores, setFilterFornecedores] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [mensagem, setMensagem] = useState("");

  useEffect(() => {
    if (mensagem){
      const timer = setTimeout(() => {
        setMensagem("");
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [mensagem]);

  useEffect(() => {
    if (categoriaID){
      const fornecedoresFiltrados = fornecedores.filter(fornecedor => 
        fornecedor.categorias.includes(parseInt(categoriaID))
      );
      setFilterFornecedores(fornecedoresFiltrados);
      setFornecedorID("");
    } else {
      setFilterFornecedores([]);
    }
  }, [categoriaID, fornecedores]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newProduto = {
      nome: nome,
      descricao: descricao,
      marca: marca,
      fabricante: fabricante,
      validadeProduto: validadeProduto,
      peso: peso,
      preco: preco,
      categoriaID: categoriaID,
      fornecedorID: fornecedorID,
    };

    fetch(apiUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newProduto),
    })
    .then(response => response.json())
    .then(data => {
      dispatch({ type: 'ADD_PRODUTO', payload: data });
      setMensagem("Produto cadastrado com sucesso!");
      clearForm();
    })
    .catch(error => {
      console.error("Erro ao cadastrar o produto:", error);
      setMensagem("Erro ao cadastrar o produto.");
    });
  };

  const clearForm = () => {
    setNome("");
    setDescricao("");
    setMarca("");
    setFabricante("");
    setValidadeProduto("");
    setPeso("");
    setPreco("");
    setCategoriaID("");
    setFornecedorID("");
    setIsEditing(false);
  };

  const handleDelete = (index) => {
    fetch(`${apiUrl}/${index}`, {
      method: 'DELETE',
    })
    .then(() => {
      dispatch({ type: 'DELETE_PRODUTO', payload: index });
      setMensagem("Produto excluÃ­do com sucesso!");
    })
    .catch(error => {
      console.error("Erro ao excluir o produto:", error);
      setMensagem("Erro ao excluir o produto.");
    });
  };

  const handleEdit = (index) => {
    const produtoToEdit = produtos[index];
    setNome(produtoToEdit.nome);
    setDescricao(produtoToEdit.descricao);
    setMarca(produtoToEdit.marca);
    setFabricante(produtoToEdit.fabricante);
    setValidadeProduto(produtoToEdit.validadeProduto);
    setPeso(produtoToEdit.peso);
    setPreco(produtoToEdit.preco);
    setCategoriaID(produtoToEdit.categoriaID);
    setFornecedorID(produtoToEdit.fornecedorID);

    const produtosCopia = [...produtos];
    produtosCopia.splice(index, 1);
    dispatch({ type: 'DELETE_PRODUTO', payload: index });
    setIsEditing(true);
  };

  const handleSave = (e) => {
    e.preventDefault();
    const updatedProduto = {
      nome,
      descricao,
      marca,
      fabricante,
      validadeProduto,
      peso,
      preco,
      categoriaID,
      fornecedorID,
    };

    fetch(`${apiUrl}/${produtos[editIndex].id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedProduto),
    })
    .then(response => response.json())
    .then(data => {
      dispatch({ type: 'UPDATE_PRODUTO', payload: { index: editingIndex, produto: data } });
      setMensagem("Produto atualizado com sucesso!");
      clearForm();
    })
    .catch(error => {
      console.error("Erro ao atualizar o produto:", error);
      setMensagem("Erro ao atualizar o produto.");
    });
  };

  return(
    <div className="form-produtos">
      <form onSubmit={isEditing ? handleSave : handleSubmit}>
        <h1>Sistema de Cadastro de Produtos</h1>
        <div className="field">
          <strong>Nome do Produto:</strong>
          <input 
            type="text" 
            value={nome} 
            onChange={(e) => setNome(e.target.value)} 
            placeholder="Ex: Adidas Aires" 
            required 
          />
        </div>
        <div className="field">
          <strong>DescriÃ§Ã£o do Produto</strong>
          <input 
            type="text" 
            value={descricao} 
            onChange={(e) => setDescricao(e.target.value)} 
            placeholder="Ex: TÃªnis Tamanho: 41-42" 
            required 
          />
        </div>
        <div className="field">
          <strong>Marca do Produto</strong>
          <input 
            type="text" 
            value={marca} 
            onChange={(e) => setMarca(e.target.value)} 
            placeholder="Ex: Adidas, Nike" 
            required 
          />
        </div>
        <div className="field">
          <strong>Fabricante do Produto</strong>
          <input 
            type="text" 
            value={fabricante} 
            onChange={(e) => setFabricante(e.target.value)} 
            placeholder="Ex: Nike - Industrias Brasileiras" 
            required 
          />
        </div>
        <div className="field">
          <strong>Validade do Produto</strong>
          <input 
            type="text" 
            value={validadeProduto} 
            onChange={(e) => setValidadeProduto(e.target.value)} 
            placeholder= "Ex: 20 de maio 2025 - (Opcional)" 
          />
        </div>
        <div className="field">
          <strong>Peso do Produto</strong>
          <input 
            type="number" 
            value={peso} 
            onChange={(e) => setPeso(e.target.value)} 
            placeholder="Ex: 125" 
            required 
          />
        </div>
        <div className="field">
          <strong>PreÃ§o do Produto</strong>
          <input 
            type="number" 
            value={preco} 
            onChange={(e) => setPreco(e.target.value)} 
            placeholder="Ex: 123" 
            required 
          />
        </div>
        <div className="field">
          <strong>Categoria do Produto</strong>
          <select 
            value={categoriaID} 
            onChange={(e) => setCategoriaID(e.target.value)} 
            required
          >
            <option value="">Selecione a Categoria</option>
            {categorias.map((categoria) => (
              <option key={categoria.id} value={categoria.id}>
                {categoria.nome}
              </option>
            ))}
          </select>
        </div>
        <div className="field">
          <strong>Fornecedor</strong>
          <select 
            value={fornecedorID} 
            onChange={(e) => setFornecedorID(e.target.value)} 
            required
          >
            <option value="">Selecione o Fornecedor</option>
            {filterFornecedores.map((fornecedor) => (
              <option key={fornecedor.id} value={fornecedor.id}>{fornecedor.nome}</option>
            ))}
          </select>
        </div>
        {mensagem && <div className="mensagem-sucesso">{mensagem}</div>}
        <div className="btn-actions">
          <button type="submit">{isEditing ? "Salvar Produto" : "Adicionar Produto"}</button>
        </div>
      </form>
      <ProdutoLista 
        produtos={produtos} 
        categorias={categorias} 
        fornecedores={fornecedores} 
        handleDelete={handleDelete} 
        handleEdit={handleEdit} 
      />
    </div>
  );
}

function ProdutoLista({ produtos, categorias, fornecedores, handleDelete, handleEdit }) {
  return (
    <div className="lista-produto">
      <h2>Produtos</h2>
      {produtos.map((produto, index) => (
        <div key={produto.id} className="product-item">
          <p><strong>Nome do Produto:</strong> {produto.nome}</p>
          <p><strong>DescriÃ§Ã£o do Produto:</strong> {produto.descricao}</p>
          <p><strong>Marca do Produto:</strong> {produto.marca}</p>
          <p><strong>Fabricante do Produto:</strong> {produto.fabricante}</p>
          <p><strong>Validade do Produto:</strong> {produto.validadeProduto}</p>
          <p><strong>Peso do Produto:</strong> {produto.peso} kg</p>
          <p><strong>PreÃ§o do Produto:</strong> R$ {produto.preco}</p>
          <p><strong>Categoria do Produto:</strong> {getCategoriaNome(categorias, produto.categoriaID)}</p>
          <p><strong>Fornecedor:</strong> {getFornecedorNome(fornecedores, produto.fornecedorID)}</p>
          <button className="delete-btn" onClick={() => handleDelete(produto.id)}>Excluir</button>
          <button className="edit-btn" onClick={() => handleEdit(index)}>Editar</button>
        </div>
      ))}
    </div>
  );
}

function getCategoriaNome(categorias, categoriaID){
  const categoria = categorias.find(cat => cat.id === parseInt(categoriaID));
  return categoria ? categoria.nome : "Categoria nÃ£o encontrada";
}

function getFornecedorNome(fornecedores, fornecedorID){
  const fornecedor = fornecedores.find(fornecedor => fornecedor.id === parseInt(fornecedorID));
  return fornecedor ? fornecedor.nome : "Fornecedor nÃ£o encontrado";
}

