// src/actions/actions.js

export const setProdutos = (produtos) => ({
    type: 'SET_PRODUTOS',
    payload: produtos
});

export const deleteProduto = (id) => ({
    type: 'DELETE_PRODUTO',
    payload: id
});

export const updateProduto = (id, updatedProduto) => ({
    type: 'UPDATE_PRODUTO',
    payload: { id, updatedProduto }
});
